const Courseinfo = () => {
    const course = 'Half Stack application development'
    const parts = [
            {
                name:'Fundamentals of React',
                exercises:10
            },
            {
                name:'Using props to pass data',
                exercises:7
            },
            {
                name:'State of a component',
                exercises: 14
            }
        ]

    return (
      <div>
        <Header course={course}/>
        <Content parts={parts} />
        {/* <Total exercises1={parts[0].exercises} exercises2={parts[2].exercises} exercises3={parts[3].exercises}/> */}
        <Total parts={parts} />
      </div>
    )
    }

const Header = (props) => {
    console.log(props)
    return(
            <h1>
                {props}
            </h1>
    )
}

const Content = (props) =>{
    return(
        <div>
            <Part parts={props} />
            {/* <Part part1 = {props.part1} part2 = {props.part2} part3 = {props.part3} exercises1={props.exercises1} exercises2={props.exercises2} exercises3={props.exercises3}/> */}
        </div>
    )
}

const Part = (parts) => {
    return(
        <>
            <p>
                {parts[0]['name']} : {parts[0]['exercises']}
            </p>
            <p>
                {parts[1]['name']} : {parts[1]['exercises']}
            </p>
            <p>
                {parts[2]['name']} : {parts[2]['exercises']}
            </p>
        </>

    )
}

const Total = (parts) => { 
    return(
        <>
        <p>
            Number of excercises : {parts[0]['exercises'] + parts[1]['exercises'] + props.parts[2]['exercises']}
        </p>
        </>
    )
}
  export default Courseinfo
